import React, { Component } from 'react';
import axios from 'axios';

class SignIn extends Component{
    constructor(props){
        super(props);
        this.state = {email:'', password: ''};
        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.KeyUp = this.KeyUp.bind(this);
    }
    KeyUp(e){
        if(e.keyCode == 13){
            axios.get('localhost:3010')
            .then()
        }
    }
    handleEmailChange(e){
        this.setState({ email: e.target.value });
    }
    handlePasswordChange(e){
        this.setState({ password: e.target.value });
    }
    
    render(){
        return (
            <div id='SignInContainer'> 
                <div>
                <h1 id='SignIn' className='center'>Sign In</h1>
                <input placeholder='Email' onChange={this.handleEmailChange} className='input'/>
                <br/>
                <input placeholder='Password' type = 'password' onChange= {this.handlePasswordChange} onKeyUp = {this.keyUp} className='input'/>
                </div>
            </div>
        );
    }
}

export default SignIn;
