import React, { Component } from 'react';

class Register extends Component{
    constructor(props){
        super(props);
        this.state = {name: '', email:'', password: '', confirm:''};
        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handleNameChange = this.handleNameChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.handleConfirmChange = this.handleConfirmChange.bind(this);
        this.KeyUp = this.KeyUp.bind(this);
    }
    KeyUp(e){
        if(e.key === 'Enter'){
            axios.post('http://localhost:3010/SignIn',{
                crossDomain:true,
                email: this.state.email,
                password: this.state.password
            })
            .then((user)=>{
                if(user){
                    console.log(user);
                }
            })
        }
    }
    handleEmailChange(e){
        this.setState({ email: e.target.value });
    }
    handleNameChange(e){
        this.setState({ name: e.target.value });
    }
    handleConfirmChange(e){
        this.setState({ confirm: e.target.value });
    }
    handlePasswordChange(e){
        this.setState({ password: e.target.value });
    }
    
    render(){
        return (
        <div id = 'SignInContainer'> 
            <div>
            <h1 className='center'>Register</h1>
            <input placeholder = 'Name' onChange={this.handleNameChange} className='input'/>
            <br/>
            <input placeholder = 'Email'  onChange={this.handleEmailChange} className='input'/>
            <br/>
            <input placeholder = 'Password'onChange={this.handlePasswordChange} className='input'/>
            <br/>
            <input placeholder = 'Confirm Password' onChange={this.handleConfirmChange} onKeyDown={this.KeyUp} className='input'/>
            </div>
        </div>);
    }
}

export default Register;
